DROP TABLE IF EXISTS actionhhd;
CREATE TABLE actionhhd ( playerid int(11) unsigned NOT NULL, handid bigint(20) unsigned NOT NULL, m int(10) unsigned DEFAULT NULL, cards varchar(6) DEFAULT NULL, position tinyint(2) DEFAULT NULL COMMENT 'Order of players acting on the hand (0-SB to TotalPlayers-1)', strategicposition tinyint(2) DEFAULT NULL COMMENT 'Stratecic Position of player SB to BU', inposflop tinyint(1) unsigned NOT NULL DEFAULT '2' COMMENT '1=IP, 0=OOP 2=N/A(no action on Round)', inposturn tinyint(1) unsigned NOT NULL DEFAULT '2' COMMENT '1=IP, 0=OOP 2=N/A(no action on Round)', inposriver tinyint(1) unsigned NOT NULL DEFAULT '2' COMMENT '1=IP, 0=OOP 2=N/A(no action on Round)', raiseinfrontpreflopfrompos tinyint(2) NOT NULL DEFAULT '-1' COMMENT 'Which position came preflop raise from', action0 varchar(64) DEFAULT NULL COMMENT 'Preflop', action1 varchar(64) DEFAULT NULL COMMENT 'Flop', action2 varchar(64) DEFAULT NULL COMMENT 'Turn', action3 varchar(64) DEFAULT NULL COMMENT 'River', sequence0 varchar(10) DEFAULT NULL COMMENT 'Preflop Action Sequence', sequence1 varchar(10) DEFAULT NULL COMMENT 'Flop Action Sequence', sequence2 varchar(10) DEFAULT NULL COMMENT 'Turn Action Sequence', sequence3 varchar(10) DEFAULT NULL COMMENT 'River Action Sequence', KEY index_gameid (handid), KEY index_playerid (playerid), KEY index_cards (cards), KEY index_seat (position) ) ;
DROP TABLE IF EXISTS gamehhd;
CREATE TABLE gamehhd ( identity bigint(20) unsigned NOT NULL AUTO_INCREMENT, sessionid bigint(20) unsigned DEFAULT NULL, gameid bigint(20) DEFAULT NULL COMMENT 'As found in the Hand History', tournamentid bigint(20) unsigned DEFAULT NULL COMMENT 'As found in the Hand History - 0 for Cashgames', tablename varchar(32) DEFAULT NULL, site varchar(16) DEFAULT NULL, bb double unsigned DEFAULT NULL, sb double unsigned DEFAULT NULL, timein timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, totalplayers tinyint(2) unsigned DEFAULT NULL, activeplayers tinyint(2) unsigned NOT NULL DEFAULT '1' COMMENT 'How many do not just fold preflop', inflop tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT 'Players on Flop', inturn tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT 'Players on Turn', inriver tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT 'Players on River', board varchar(16) DEFAULT NULL, sequence0 varchar(500) DEFAULT NULL COMMENT 'Preflop Action Sequence for all players', sequence1 varchar(500) DEFAULT NULL COMMENT 'Flop Action Sequence for all players', sequence2 varchar(500) DEFAULT NULL COMMENT 'Turn Action Sequence for all players', sequence3 varchar(500) DEFAULT NULL COMMENT 'River Action Sequence for all players', PRIMARY KEY (identity), KEY index_gameid (gameid), KEY index_level (bb), KEY index_sessionid (sessionid) );
DROP TABLE IF EXISTS playerhhd;
CREATE TABLE playerhhd ( playerid bigint(11) NOT NULL AUTO_INCREMENT, site varchar(32) DEFAULT NULL, nickname varchar(32) DEFAULT NULL, PRIMARY KEY (playerid), KEY index_nickname(nickname) );

DROP TABLE IF EXISTS actionhhd; 

CREATE TABLE actionhhd(
       playerid int(11) NOT NULL,
       handid bigint(20) NOT NULL,
       m int(10) DEFAULT NULL,
       cards varchar(6) DEFAULT NULL,
       position tinyint(2) DEFAULT NULL,
       strategicposition tinyint(2) DEFAULT NULL,
       inposflop tinyint(1) NOT NULL  default '2',
       inposturn tinyint(1) NOT NULL default '2',
       inposriver tinyint(1) NOT NULL default '2',
       raiseinfrontpreflopfrompos tinyint(2) NOT NULL default '-1',
       action0 varchar(64) DEFAULT NULL,
       action1 varchar(64) DEFAULT NULL,
       action2 varchar(64) DEFAULT NULL,
       action3 varchar(64) DEFAULT NULL,
       sequence0 varchar(10) DEFAULT NULL,
       sequence1 varchar(10) DEFAULT NULL,
       sequence2 varchar(10) DEFAULT NULL,
       sequence3 varchar(10) DEFAULT NULL
       );

CREATE INDEX handid_index ON actionhhd (handid);       
CREATE INDEX playerid_index ON actionhhd (playerid);


DROP TABLE IF EXISTS gamehhd;
CREATE TABLE gamehhd (
	identity INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
	sessionid bigint(20) DEFAULT NULL,
	gameid bigint(20) DEFAULT NULL,
	tournamentid bigint(20) DEFAULT NULL,
	tablename varchar(32) DEFAULT NULL,
	site varchar(16) DEFAULT NULL,
	bb double unsigned DEFAULT NULL,
	sb double unsigned DEFAULT NULL,
	timein timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
	totalplayers tinyint(2) DEFAULT NULL,
	activeplayers tinyint(2) DEFAULT NULL,
	inflop tinyint(2) DEFAULT NULL,
	inturn tinyint(2) DEFAULT NULL,
	inriver tinyint(2) DEFAULT NULL,
	board varchar(16) DEFAULT NULL,
	sequence0 varchar(500) DEFAULT NULL,
	sequence1 varchar(500) DEFAULT NULL,
	sequence2 varchar(500) DEFAULT NULL,
	sequence3 varchar(500) DEFAULT NULL
);

CREATE INDEX identity_index ON gamehhd (identity);
CREATE INDEX gameid_index ON gamehhd (gameid);
CREATE INDEX bb_index ON gamehhd (bb);
CREATE INDEX totalplayers_index ON gamehhd (totalplayers);
CREATE INDEX activeplayers_index ON gamehhd (activeplayers);
CREATE INDEX inflop_index ON gamehhd (inflop);


DROP TABLE IF EXISTS playerhhd;

CREATE TABLE playerhhd(
     playerid INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
     site varchar(32) DEFAULT NULL,
     nickname varchar(32) DEFAULT NULL
);

CREATE INDEX nickname_index ON playerhhd (nickname);
